/* This function creates a two data sets, with different topics,
   the same x values and different y values*/

// Change the scale factor each time.
var scale = context.get('scale') || 0;
if (msg.topic == "Reset") {
    scale = 0;
}
scale++;
context.set('scale', scale);

msg.payload = [];       // This will be an array of {topic, data} objects
var dataPoints1 = [];   // These will be the first array of data points 
var dataPoints2 = [];   // These will be the second array of data points 
var numPoints = 20;     // We will create one more than this.

// For the first data set create a sine wave
for (var i=0; i<=numPoints; i++) {
    var point = {};
    point.x = i;
    point.y = Math.sin(2 * 3.14 * (i/numPoints)) * scale/10;
    // build the data array    
    dataPoints1.push(point);
}

// For the second data set create a triangle
for (var i=0; i<=numPoints; i++) {
    var point = {};
    point.x = i;
    if (i<(numPoints/2)) {
        // ramp up
        point.y = i * scale/10;
    }
    else {
        // ramp down
        point.y = (numPoints - i) * scale/10;
    }
    dataPoints2.push(point);    // add to the array
}

msg.action = "load";    // This instructs the chart node to paint the data

// The payload is an array of two {topic, data} objects
msg.payload = [{topic: "Sin", data: dataPoints1},
                {topic: "Triangle", data: dataPoints2}];

return msg;

